var query=require('querystring')
module.exports=function(){
    return function (req,res,reslove) {
        var datas="";
        req.body={};
       if(req.headers["content-type"]=="application/x-www-form-urlencoded"){
        req.on("data",function(data){
            datas+=data;
        })
        req.on("end",function () {
            var qobj=(query.parse(datas));
            for(var i in qobj){
                req.body[i]=qobj[i];
            }
            reslove();

        })
       }else{
           reslove();
       }

    }
}